module.exports = function override(config, env) {
  // Find the rule that handles JavaScript files
  const babelLoader = config.module.rules[1].oneOf.find(
    rule => rule.loader && rule.loader.includes('babel-loader')
  );

  // Modify the exclude field to include the @emailjs/browser package
  babelLoader.exclude = /node_modules\/(?!(@emailjs\/browser)\/).*/;

  return config;
};
